// 获取地址栏参数
exports.getUrlParam = (name) => {  
    let reg = new RegExp("(^|&)"+ name +"=([^&]*)(&|$)");  
    let r = window.location.search.substr(1).match(reg);  
    if (r!=null) 
      return decodeURI(r[2]); 
    return null;  
}
